from Crypto.Util.number import *
from secret import FLAG
import random

e = 1337
p = [getPrime(512) for i in range(20)]
q = [getPrime(512) for i in range(20)]

N = random.choice(p) * getPrime(512)

m = bytes_to_long(FLAG)
c = pow(m, e, N)

list_N = [p[i]*q[i] for i in range(20)]
print(f"c = {c}")
print(f"N = {N}")
print(f"list_N = {list_N}")

